mapper.select:
SELECT inn.inn_no, inn.inn_addr, inn.inn_type, (SELECT inn_filepath FROM inn_file_tbl innfile WHERE inn.inn_no = innfile.inn_no AND ROWNUM = 1) as file_path, (SELECT min(room_price) FROM room_tbl r WHERE r.inn_no=inn.inn_no) room_price, (SELECT partner_name FROM partner_tbl p WHERE inn.partner_no = p.partner_no) partner_name, (SELECT COUNT(*) FROM like_tbl WHERE like_type=1 AND ref_no = inn.inn_no) like_count, (SELECT COUNT(*) FROM review_tbl WHERE review_type=1 AND ref_no = inn.inn_no) review_count FROM inn_tbl inn WHERE INN_ADDR LIKE '%'||#{innAddr}||'%' AND (SELECT COUNT(room_max_people) FROM room_tbl r WHERE room_max_people >= 2 AND r.inn_no=inn.inn_no) >= 1
AND CHECK_BOOKING(inn.inn_no,#{checkInDate},#{checkOutDate}) = 0
AND INN_TYPE=#{innType}
AND (SELECT min(room_price) FROM room_tbl r WHERE r.inn_no=inn.inn_no) BETWEEN #{minPrice} AND #{maxPrice}
AND (SELECT COUNT (hashtag) FROM ROOM_HASHTAG_TBL rt WHERE room_no in (SELECT room_no FROM room_tbl r WHERE r.inn_no =inn.inn_no AND rt.room_no=room_no) AND hashtag like #{hashTagItem}) > 0
AND (SELECT COUNT(option_no) FROM ROOM_OPTION_TBL ro WHERE room_no in (SELECT room_no FROM room_tbl r WHERE r.inn_no = inn.inn_no AND ro.room_no = room_no) AND option_no = #{optionItem}) > 0
